import React from 'react';
import { Modal, Descriptions, Tabs, Tag } from 'antd';
import { PhoneOutlined, MailOutlined, GlobalOutlined } from '@ant-design/icons';
import KontakTab from './PemasokTab/KontakTab';

const { TabPane } = Tabs;

const PemasokDetail = ({ visible, onCancel, pemasok }) => {
  if (!pemasok) return null;

  const formatCurrency = (value) => {
    return value?.toLocaleString('id-ID') || '0';
  };

  return (
    <Modal
      title={`Detail Pemasok - ${pemasok.nama}`}
      visible={visible}
      onCancel={onCancel}
      width={900}
      footer={null}
      bodyStyle={{ padding: 0 }}
    >
      <Tabs defaultActiveKey="1">
        <TabPane tab="Informasi Umum" key="1">
          <div style={{ padding: 24 }}>
            <Descriptions bordered column={2}>
              <Descriptions.Item label="Status" span={2}>
                {pemasok.non_aktif ? (
                  <Tag color="red">Non-Aktif</Tag>
                ) : (
                  <Tag color="green">Aktif</Tag>
                )}
              </Descriptions.Item>
              <Descriptions.Item label="No Pemasok">
                {pemasok.no_pemasok}
              </Descriptions.Item>
              <Descriptions.Item label="Nama">{pemasok.nama}</Descriptions.Item>
              <Descriptions.Item label="Alamat" span={2}>
                <div style={{ whiteSpace: 'pre-line' }}>{pemasok.alamat}</div>
                <div>
                  {[pemasok.kota, pemasok.provinsi, pemasok.kode_pos]
                    .filter(Boolean)
                    .join(', ')}
                </div>
                <div>{pemasok.negara}</div>
              </Descriptions.Item>
              <Descriptions.Item label="Kontak">
                <div>
                  <PhoneOutlined /> {pemasok.telepon}
                </div>
                <div>
                  <PhoneOutlined /> {pemasok.faksimili || '-'}
                </div>
                <div>
                  <MailOutlined /> {pemasok.email || '-'}
                </div>
                <div>
                  <GlobalOutlined /> {pemasok.halaman_web || '-'}
                </div>
              </Descriptions.Item>
              <Descriptions.Item label="Orang Kontak">
                {pemasok.kontak || '-'}
              </Descriptions.Item>
            </Descriptions>
          </div>
        </TabPane>

        <TabPane tab="Termin Pembayaran" key="2">
          <div style={{ padding: 24 }}>
            <Descriptions bordered column={2}>
              <Descriptions.Item label="Termin Pembayaran">
                {pemasok.payment_term?.name || '-'}
                {pemasok.payment_term && (
                  <div
                    style={{
                      marginTop: 8,
                      padding: 8,
                      background: '#f5f5f5',
                      borderRadius: 4,
                    }}
                  >
                    <div>
                      Bayar dalam: {pemasok.payment_term.pay_within_days} hari
                    </div>
                    <div>
                      Diskon: {pemasok.payment_term.discount_percent || 0}%
                    </div>
                    <div>Jatuh tempo: {pemasok.payment_term.due_days} hari</div>
                  </div>
                )}
              </Descriptions.Item>
              <Descriptions.Item label="Mata Uang">
                {pemasok.currency?.name || '-'} (
                {pemasok.currency?.symbol || ''})
              </Descriptions.Item>
              <Descriptions.Item label="Saldo Awal">
                {formatCurrency(pemasok.saldo_awal)}{' '}
                {pemasok.currency?.symbol || ''}
              </Descriptions.Item>
              <Descriptions.Item label="Per Tanggal">
                {pemasok.saldo_awal_tanggal || '-'}
              </Descriptions.Item>
              <Descriptions.Item label="Default Keterangan" span={2}>
                <div style={{ whiteSpace: 'pre-line' }}>
                  {pemasok.default_keterangan || '-'}
                </div>
              </Descriptions.Item>
              <Descriptions.Item label="Pajak 1">
                {pemasok.pajak1
                  ? `${pemasok.pajak1.nama} (${pemasok.pajak1.nilai}%)`
                  : '-'}
              </Descriptions.Item>
              <Descriptions.Item label="Pajak 2">
                {pemasok.pajak2
                  ? `${pemasok.pajak2.nama} (${pemasok.pajak2.nilai}%)`
                  : '-'}
              </Descriptions.Item>
              <Descriptions.Item label="NPWP Pemasok">
                {pemasok.npwp_pemasok || '-'}
              </Descriptions.Item>
              <Descriptions.Item label="Tax Type">
                {pemasok.tax_type?.nama || '-'}
              </Descriptions.Item>
              <Descriptions.Item label="Pesan" span={2}>
                <div style={{ whiteSpace: 'pre-line' }}>
                  {pemasok.pesan || '-'}
                </div>
              </Descriptions.Item>
            </Descriptions>
          </div>
        </TabPane>

        <TabPane tab="Kontak" key="3">
          <div style={{ padding: 24 }}>
            <KontakTab pemasokId={pemasok.id} readOnly />
          </div>
        </TabPane>

        <TabPane tab="Catatan" key="4">
          <div style={{ padding: 24 }}>
            <div
              style={{
                padding: 16,
                background: '#f5f5f5',
                borderRadius: 4,
                whiteSpace: 'pre-line',
                minHeight: 200,
              }}
            >
              {pemasok.catatan || 'Tidak ada catatan'}
            </div>
          </div>
        </TabPane>
      </Tabs>
    </Modal>
  );
};

export default PemasokDetail;
