import { useState, useRef, useEffect } from 'react';
import {
  Building,
  FileText,
  FilePlus,
  Currency,
  JournalText,
  JournalBookmark,
  Cash,
  Receipt,
  Cart,
  Box,
} from 'lucide-react';

const menuItems = [
  {
    id: 'company-submenu',
    icon: <Building size={18} />,
    title: 'Perusahaan',
    submenu: [
      { icon: <Building />, title: 'Company Profile' },
      { icon: <Receipt />, title: 'Menu 1' },
    //   { icon: <Truck />, title: 'Menu 2' },
    ],
  },
  {
    id: 'essential-data',
    icon: <FileText size={18} />,
    title: 'Essential Data',
    submenu: [
      { icon: <Currency />, title: 'Currency' },
      { icon: <JournalText />, title: 'Chart of Account' },
    ],
  },
  {
    id: 'data-entry',
    icon: <FilePlus size={18} />,
    title: 'Data Entry',
    submenu: [
      { icon: <JournalBookmark />, title: 'General Ledger' },
      { icon: <Cash />, title: 'Cash & Bank' },
      { icon: <Receipt />, title: 'Sales' },
      { icon: <Cart />, title: 'Purchase' },
      { icon: <Box />, title: 'Inventory' },
    ],
  },
];

function Sidebar() {
  const [openMenu, setOpenMenu] = useState(null);
  const submenuRefs = useRef({});

  const toggleSubmenu = (id) => {
    setOpenMenu((prev) => (prev === id ? null : id));
  };

  useEffect(() => {
    const closeOnClickOutside = (e) => {
      if (
        !Object.values(submenuRefs.current).some((ref) =>
          ref?.contains(e.target)
        )
      ) {
        setOpenMenu(null);
      }
    };
    document.addEventListener('click', closeOnClickOutside);
    return () => document.removeEventListener('click', closeOnClickOutside);
  }, []);

  return (
    <>
      <div className="fixed top-0 left-0 w-[50px] h-screen bg-[#2c3e50] flex flex-col items-center z-50 pt-4 shadow-md">
        {menuItems.map((item) => (
          <div
            key={item.id}
            className="text-gray-300 hover:bg-[#34495e] hover:text-blue-400 transition-all p-2 rounded-md my-1 cursor-pointer"
            onClick={() => toggleSubmenu(item.id)}
          >
            {item.icon}
          </div>
        ))}
      </div>

      {menuItems.map((item) => (
        <div
          key={item.id}
          ref={(el) => (submenuRefs.current[item.id] = el)}
          className={`absolute left-[60px] top-[80px] w-[400px] z-40 bg-white shadow-lg border rounded-lg transition-all ${
            openMenu === item.id ? 'block animate-fadeIn' : 'hidden'
          }`}
        >
          <div className="bg-gray-100 px-4 py-2 border-b">
            <h3 className="text-sm font-semibold uppercase text-gray-700">
              {item.title}
            </h3>
          </div>
          <div className="p-4 max-h-[350px] overflow-y-auto">
            <div className="grid grid-cols-3 gap-3">
              {item.submenu.map((sub, index) => (
                <a
                  key={index}
                  href="#"
                  className="border text-sm text-gray-600 rounded-md px-2 py-3 flex flex-col items-center hover:bg-blue-50 hover:text-blue-600 hover:shadow transition"
                >
                  <div className="mb-1">{sub.icon}</div>
                  <div className="text-[11px] text-center">{sub.title}</div>
                </a>
              ))}
            </div>
          </div>
        </div>
      ))}
    </>
  );
}

export default Sidebar;
