import React, { useEffect, useState } from 'react';
import Layout from '../../components/Layout';

const NamaTipeAkun = () => {
  const [tipeAkuns, setTipeAkuns] = useState([]);
  const [kategoris, setKategoris] = useState([]);
  const [form, setForm] = useState({ id: null, nama: '', kategori_id: '' });
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetchTipeAkuns();
    fetchKategoris();
  }, []);

  const fetchTipeAkuns = async () => {
    const res = await fetch('/api/tipe-akun');
    const data = await res.json();
    setTipeAkuns(data);
  };

  const fetchKategoris = async () => {
    const res = await fetch('/api/kategoris'); // disesuaikan dgn route
    const data = await res.json();
    setKategoris(data);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = isEditing ? 'PUT' : 'POST';
    const url = isEditing ? `/api/tipe-akun/${form.id}` : '/api/tipe-akun';

    await fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form),
    });

    setForm({ id: null, nama: '', kategori_id: '' });
    setIsEditing(false);
    fetchTipeAkuns();
  };

  const handleEdit = (tipeAkun) => {
    setForm({
      id: tipeAkun.id,
      nama: tipeAkun.nama,
      kategori_id: tipeAkun.kategori_id,
    });
    setIsEditing(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Yakin hapus data?')) {
      await fetch(`/api/tipe-akun/${id}`, { method: 'DELETE' });
      fetchTipeAkuns();
    }
  };

  return (
    <Layout>
      <div className="p-6">
        <h2 className="text-2xl font-bold mb-4">Master Tipe Akun</h2>

        <form onSubmit={handleSubmit} className="space-y-4 mb-6">
          <input
            type="text"
            name="nama"
            value={form.nama}
            onChange={handleChange}
            placeholder="Nama Tipe Akun"
            className="border p-2 w-full rounded"
            required
          />
          <select
            name="kategori_id"
            value={form.kategori_id}
            onChange={handleChange}
            className="border p-2 w-full rounded"
            required
          >
            <option value="">-- Pilih Kategori --</option>
            {kategoris.map((kat) => (
              <option key={kat.id} value={kat.id}>
                {kat.nama}
              </option>
            ))}
          </select>
          <button
            type="submit"
            className="bg-blue-600 text-white px-4 py-2 rounded"
          >
            {isEditing ? 'Update' : 'Simpan'}
          </button>
        </form>

        <table className="w-full border border-collapse">
          <thead>
            <tr className="bg-gray-200 text-left">
              <th className="border px-2 py-1">No</th>
              <th className="border px-2 py-1">Nama Tipe Akun</th>
              <th className="border px-2 py-1">Kategori</th>
              <th className="border px-2 py-1">Kode</th>
              <th className="border px-2 py-1">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {tipeAkuns.map((item, index) => (
              <tr key={item.id} className="hover:bg-gray-50">
                <td className="border px-2 py-1 text-center">{index + 1}</td>
                <td className="border px-2 py-1">{item.nama}</td>
                <td className="border px-2 py-1">{item.kategori_nama}</td>
                <td className="border px-2 py-1">{item.kode}</td>
                <td className="border px-2 py-1 space-x-2">
                  <button
                    onClick={() => handleEdit(item)}
                    className="bg-yellow-500 text-white px-3 py-1 rounded"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(item.id)}
                    className="bg-red-600 text-white px-3 py-1 rounded"
                  >
                    Hapus
                  </button>
                </td>
              </tr>
            ))}
            {tipeAkuns.length === 0 && (
              <tr>
                <td colSpan="5" className="text-center py-4">
                  Tidak ada data.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </Layout>
  );
};

export default NamaTipeAkun;
