import React, { useState, useEffect } from 'react';
import {
  Modal,
  Tabs,
  Form,
  message,
  Spin,
  Button, 
} from 'antd';
import AlamatTab from './PemasokTab/AlamatTab';
import TerminTab from './PemasokTab/TerminTab';
import KontakTab from './PemasokTab/KontakTab';
import CatatanTab from './PemasokTab/CatatanTab';
import api from '../../axios';
import moment from 'moment';

const { TabPane } = Tabs;

const PemasokForm = ({ visible, onCancel, onSuccess, pemasok, type }) => {
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [activeTab, setActiveTab] = useState('1');
  const [paymentTerms, setPaymentTerms] = useState([]);
  const [currencies, setCurrencies] = useState([]);
  const [taxes, setTaxes] = useState([]);
  const [typeTaxes, setTypeTaxes] = useState([]);

  useEffect(() => {
    if (visible) {
      fetchDropdownData();

      if (type === 'edit' && pemasok) {
        const values = {
          ...pemasok,
          payment_term_id: pemasok.payment_term_id,
          currency_id: pemasok.currency_id,
          pajak1_id: pemasok.pajak1_id,
          pajak2_id: pemasok.pajak2_id,
          tax_type_id: pemasok.tax_type_id,
          saldo_awal_tanggal: pemasok.saldo_awal_tanggal
            ? moment(pemasok.saldo_awal_tanggal)
            : null,
        };

        form.setFieldsValue(values);
      } else {
        form.resetFields();
        form.setFieldsValue({
          non_aktif: false,
          saldo_awal: 0,
        });
      }
    }
  }, [visible, type, pemasok, form]); // âœ… Tambahkan `form` untuk menghindari warning

  const fetchDropdownData = async () => {
    try {
      setLoading(true);
      const [termsRes, currenciesRes, taxesRes, typeTaxesRes] =
        await Promise.all([
          api.get('/api/payment_terms'),
          api.get('/api/currencies'),
          api.get('/api/mst_tax'),
          api.get('/api/type_taxes'),
        ]);

      setPaymentTerms(termsRes.data.data || termsRes.data);
      setCurrencies(currenciesRes.data.data || currenciesRes.data);
      setTaxes(taxesRes.data.data || taxesRes.data);
      setTypeTaxes(typeTaxesRes.data.data || typeTaxesRes.data);
    } catch (error) {
      console.error('Error fetching dropdown data:', error);
      message.error('Gagal mengambil data referensi');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async () => {
    try {
      setSubmitting(true);
      const values = await form.validateFields();

      const formattedValues = {
        ...values,
        saldo_awal_tanggal: values.saldo_awal_tanggal?.format('YYYY-MM-DD'),
      };

      if (type === 'create') {
        await api.post('/api/pemasoks', formattedValues);
        message.success('Pemasok berhasil ditambahkan');
      } else {
        await api.put(`/api/pemasoks/${pemasok.id}`, formattedValues);
        message.success('Pemasok berhasil diperbarui');
      }

      onSuccess();
    } catch (error) {
      console.error('Error submitting form:', error);
      if (error.response?.data?.errors) {
        const errors = error.response.data.errors;
        Object.keys(errors).forEach((key) => {
          message.error(errors[key][0]);
        });
      } else {
        message.error('Terjadi kesalahan saat menyimpan data');
      }
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <Modal
      title={type === 'create' ? 'Tambah Pemasok' : 'Edit Pemasok'}
      visible={visible}
      onCancel={onCancel}
      width={900}
      footer={[
        <Button key="back" onClick={onCancel} disabled={submitting}>
          Batal
        </Button>,
        <Button
          key="submit"
          type="primary"
          loading={submitting}
          onClick={handleSubmit}
        >
          Simpan
        </Button>,
      ]}
      destroyOnClose
    >
      <Spin spinning={loading}>
        <Form form={form} layout="vertical">
          <Tabs
            activeKey={activeTab}
            onChange={setActiveTab}
            tabPosition="left"
            style={{ minHeight: 400 }}
          >
            <TabPane tab="Alamat" key="1">
              <AlamatTab form={form} type={type} />
            </TabPane>
            <TabPane tab="Termin" key="2">
              <TerminTab
                form={form}
                paymentTerms={paymentTerms}
                currencies={currencies}
                taxes={taxes}
                typeTaxes={typeTaxes}
              />
            </TabPane>
            <TabPane tab="Kontak" key="3">
              <KontakTab pemasokId={pemasok?.id} refreshData={onSuccess} />
            </TabPane>
            <TabPane tab="Catatan" key="4">
              <CatatanTab form={form} />
            </TabPane>
          </Tabs>
        </Form>
      </Spin>
    </Modal>
  );
};

export default PemasokForm;
