import { useEffect, useState } from 'react';
import api from '../../../axios';

const TerminTab = ({ formData, onChange }) => {
  const [paymentTerms, setPaymentTerms] = useState([]);
  const [selectedTerm, setSelectedTerm] = useState(null);
  const [currencies, setCurrencies] = useState([]);
  const [taxes, setTaxes] = useState([]);
  const [taxTypes, setTaxTypes] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    const term = paymentTerms.find(
      (t) => t.id === parseInt(formData.termin_id)
    );
    setSelectedTerm(term || null);
  }, [formData.termin_id, paymentTerms]);

  const fetchData = async () => {
    try {
      const [termRes, currRes, taxRes, typeRes] = await Promise.all([
        api.get('/api/payment_terms'),
        api.get('/currencies'),
        api.get('/mst_tax'),
        api.get('/type_taxes'),
      ]);
      setPaymentTerms(termRes.data);
      setCurrencies(currRes.data);
      setTaxes(taxRes.data);
      setTaxTypes(typeRes.data);
    } catch (err) {
      console.error('Fetch error:', err);
    }
  };

  return (
    <div>
      {/* Termin */}
      <div className="row mb-3">
        <div className="col-md-6">
          <label className="form-label">Termin</label>
          <select
            className="form-select"
            name="termin_id"
            value={formData.termin_id}
            onChange={onChange}
          >
            <option value="">-- Pilih Termin --</option>
            {paymentTerms.map((term) => (
              <option key={term.id} value={term.id}>
                {term.name}
              </option>
            ))}
          </select>
        </div>
      </div>

      {selectedTerm && (
        <div className="row mb-3">
          <div className="col-md-4">
            <label className="form-label">Pay Within Days</label>
            <input
              type="text"
              className="form-control"
              value={selectedTerm.pay_within_days}
              readOnly
            />
          </div>
          <div className="col-md-4">
            <label className="form-label">Discount %</label>
            <input
              type="text"
              className="form-control"
              value={selectedTerm.discount_percent}
              readOnly
            />
          </div>
          <div className="col-md-4">
            <label className="form-label">Due Days</label>
            <input
              type="text"
              className="form-control"
              value={selectedTerm.due_days}
              readOnly
            />
          </div>
        </div>
      )}

      {/* Mata Uang & Saldo */}
      <div className="row mb-3">
        <div className="col-md-6">
          <label className="form-label">Mata Uang</label>
          <select
            className="form-select"
            name="currency_id"
            value={formData.currency_id}
            onChange={onChange}
          >
            <option value="">-- Pilih Mata Uang --</option>
            {currencies.map((c) => (
              <option key={c.id} value={c.id}>
                {c.name}
              </option>
            ))}
          </select>
        </div>
        <div className="col-md-3">
          <label className="form-label">Saldo Awal</label>
          <input
            type="number"
            className="form-control"
            name="saldo_awal"
            value={formData.saldo_awal}
            onChange={onChange}
          />
        </div>
        <div className="col-md-3">
          <label className="form-label">Per Tanggal</label>
          <input
            type="date"
            className="form-control"
            name="saldo_awal_tanggal"
            value={formData.saldo_awal_tanggal}
            onChange={onChange}
          />
        </div>
      </div>

      {/* Keterangan, Pajak, NPWP */}
      <div className="mb-3">
        <label className="form-label">Default Keterangan</label>
        <textarea
          className="form-control"
          name="default_keterangan"
          rows="2"
          value={formData.default_keterangan}
          onChange={onChange}
        ></textarea>
      </div>

      <div className="row mb-3">
        <div className="col-md-4">
          <label className="form-label">Pajak 1</label>
          <select
            className="form-select"
            name="pajak_1_id"
            value={formData.pajak_1_id}
            onChange={onChange}
          >
            <option value="">-- Pilih Pajak --</option>
            {taxes.map((t) => (
              <option key={t.id} value={t.id}>
                {t.nama}
              </option>
            ))}
          </select>
        </div>
        <div className="col-md-4">
          <label className="form-label">Pajak 2</label>
          <select
            className="form-select"
            name="pajak_2_id"
            value={formData.pajak_2_id}
            onChange={onChange}
          >
            <option value="">-- Pilih Pajak --</option>
            {taxes.map((t) => (
              <option key={t.id} value={t.id}>
                {t.nama}
              </option>
            ))}
          </select>
        </div>
        <div className="col-md-4">
          <label className="form-label">NPWP Pemasok</label>
          <input
            type="text"
            className="form-control"
            name="npwp"
            value={formData.npwp}
            onChange={onChange}
          />
        </div>
      </div>

      {/* Tax Type & Pesan */}
      <div className="mb-3">
        <label className="form-label">Tax Type</label>
        <select
          className="form-select"
          name="tax_type_id"
          value={formData.tax_type_id}
          onChange={onChange}
        >
          <option value="">-- Pilih Jenis Pajak --</option>
          {taxTypes.map((t) => (
            <option key={t.id} value={t.id}>
              {t.nama}
            </option>
          ))}
        </select>
      </div>

      <div className="mb-3">
        <label className="form-label">Pesan</label>
        <textarea
          className="form-control"
          name="pesan"
          rows="2"
          value={formData.pesan}
          onChange={onChange}
        ></textarea>
      </div>
    </div>
  );
};

export default TerminTab;
