import { useCallback, useEffect, useState } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

const accountTypes = [
  'Cash/Bank',
  'Account Receivable',
  'Inventory',
  'Other Current Asset',
  'Fixed Asset',
  'Accumulated Depreciation',
  'Account Payable',
  'Other Current Liability',
  'Long Term Liability',
  'Equity',
  'Revenue',
  'Cost of goods Sold',
  'Expense',
  'Other Expense',
  'Other Income',
];

const initialForm = (today) => ({
  name: '',
  account_type: '',
  is_sub_account: false,
  parent_id: '',
  opening_balance: '',
  as_of_date: today,
});

export default function ChartOfAccount() {
  const today = new Date().toISOString().split('T')[0];

  const [accounts, setAccounts] = useState([]);
  const [parents, setParents] = useState([]);
  const [form, setForm] = useState(initialForm(today));
  const [editingId, setEditingId] = useState(null);
  const [filterType, setFilterType] = useState('');
  const [search, setSearch] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);

  const fetchAccounts = useCallback(async () => {
    try {
      const res = await api.get('/api/chart-of-accounts', {
        params: { type: filterType, search },
      });
      setAccounts(res.data);
      setParents(res.data.filter((a) => !a.is_sub_account));
    } catch (err) {
      console.error('Failed to fetch accounts:', err);
    }
  }, [filterType, search]);

  useEffect(() => {
    fetchAccounts();
  }, [fetchAccounts]);

  const handleChange = (e) => {
    const { name, type, checked, value } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const resetForm = () => {
    setForm(initialForm(today));
    setEditingId(null);
    setShowModal(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const {
      name,
      account_type,
      is_sub_account,
      parent_id,
      opening_balance,
      as_of_date,
    } = form;

    if (!name.trim()) return alert('Nama akun wajib diisi.');
    if (!account_type) return alert('Tipe akun wajib dipilih.');
    if (!as_of_date) return alert('Tanggal wajib diisi.');
    if (is_sub_account && !parent_id)
      return alert('Pilih akun induk jika ini sub akun.');
    if (opening_balance && isNaN(parseFloat(opening_balance))) {
      return alert('Opening balance harus berupa angka.');
    }

    const payload = {
      ...form,
      opening_balance: opening_balance === '' ? 0 : parseFloat(opening_balance),
    };

    try {
      if (editingId) {
        await api.put(`/api/chart-of-accounts/${editingId}`, payload);
      } else {
        await api.post('/api/chart-of-accounts', payload);
      }

      resetForm();
      fetchAccounts();
    } catch (err) {
      console.error('Save error:', err);
      alert('Terjadi kesalahan saat menyimpan akun.');
    }
  };

  const handleEdit = (account) => {
    setForm({
      name: account.name,
      account_type: account.account_type,
      is_sub_account: account.is_sub_account,
      parent_id: account.parent_id || '',
      opening_balance: account.opening_balance,
      as_of_date: account.as_of_date,
    });
    setEditingId(account.id);
    setShowModal(true);
  };

  const handleDelete = async () => {
    try {
      await api.delete(`/api/chart-of-accounts/${confirmDeleteId}`);
      setConfirmDeleteId(null);
      fetchAccounts();
    } catch (err) {
      console.error('Delete error:', err);
      alert('Gagal menghapus akun.');
    }
  };

  const buildAccountTree = (list, parentId = null, level = 0) => {
    return list
      .filter((a) => a.parent_id === parentId)
      .flatMap((a) => [
        { ...a, level },
        ...buildAccountTree(list, a.id, level + 1),
      ]);
  };

  const treeData = buildAccountTree(accounts);

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold">Chart of Accounts</h2>
          <button
            onClick={() => {
              resetForm();
              setShowModal(true);
            }}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            + New
          </button>
        </div>

        <div className="mb-4 flex gap-2">
          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            className="border p-2 rounded"
          >
            <option value="">All Types</option>
            {accountTypes.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
          <input
            placeholder="Search by name"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="border p-2 rounded w-full"
          />
        </div>

        <table className="w-full border text-sm">
          <thead className="bg-gray-100 text-left">
            <tr>
              <th className="border p-2">Account No</th>
              <th className="border p-2">Name</th>
              <th className="border p-2">Type</th>
              <th className="border p-2">Balance</th>
              <th className="border p-2 text-center">Action</th>
            </tr>
          </thead>
          <tbody>
            {treeData.length > 0 ? (
              treeData.map((a) => (
                <tr key={a.id}>
                  <td className="border p-2">
                    {' '.repeat(a.level)}
                    {a.account_no}
                  </td>
                  <td className="border p-2">
                    {' '.repeat(a.level)}
                    {a.name}
                  </td>
                  <td className="border p-2">{a.account_type}</td>
                  <td className="border p-2 text-right">
                    {parseFloat(a.opening_balance || 0).toLocaleString()}
                  </td>
                  <td className="border p-2 text-center space-x-2">
                    <button
                      onClick={() => handleEdit(a)}
                      className="text-blue-600 hover:text-blue-800"
                      title="Edit"
                    >
                      ✎
                    </button>
                    <button
                      onClick={() => setConfirmDeleteId(a.id)}
                      className="text-red-600 hover:text-red-800"
                      title="Delete"
                    >
                      🗑
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={5} className="text-center text-gray-500 py-4">
                  Data tidak ditemukan
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {/* Modal Form */}
        {showModal && (
          <div className="fixed inset-0 z-50 bg-black/20 flex items-center justify-center">
            <div className="bg-white p-6 rounded shadow-lg w-full max-w-lg relative">
              <button
                className="absolute top-2 right-2 text-gray-400 hover:text-black"
                onClick={() => setShowModal(false)}
              >
                ✕
              </button>
              <h3 className="text-lg font-bold mb-4">
                {editingId ? 'Edit Account' : 'Create New Account'}
              </h3>
              <form onSubmit={handleSubmit} className="space-y-3">
                <input
                  name="name"
                  value={form.name}
                  onChange={handleChange}
                  placeholder="Name"
                  className="border p-2 w-full"
                />
                <select
                  name="account_type"
                  value={form.account_type}
                  onChange={handleChange}
                  className="border p-2 w-full"
                >
                  <option value="">-- Select Account Type --</option>
                  {accountTypes.map((type) => (
                    <option key={type} value={type}>
                      {type}
                    </option>
                  ))}
                </select>
                <label className="flex items-center gap-2">
                  <input
                    type="checkbox"
                    name="is_sub_account"
                    checked={form.is_sub_account}
                    onChange={handleChange}
                  />
                  Sub Account
                </label>
                {form.is_sub_account && (
                  <select
                    name="parent_id"
                    value={form.parent_id}
                    onChange={handleChange}
                    className="border p-2 w-full"
                    disabled={!form.account_type}
                  >
                    {!form.account_type ? (
                      <option value="">Pilih tipe akun terlebih dahulu</option>
                    ) : (
                      <>
                        <option value="">-- Pilih Akun Induk --</option>
                        {parents
                          .filter((p) => p.account_type === form.account_type)
                          .map((p) => (
                            <option key={p.id} value={p.id}>
                              {p.name}
                            </option>
                          ))}
                      </>
                    )}
                  </select>
                )}
                <input
                  type="number"
                  name="opening_balance"
                  value={form.opening_balance}
                  onChange={handleChange}
                  placeholder="Opening Balance"
                  className="border p-2 w-full"
                />
                <input
                  type="date"
                  name="as_of_date"
                  value={form.as_of_date}
                  onChange={handleChange}
                  className="border p-2 w-full"
                />
                <div className="flex justify-end gap-2">
                  <button
                    type="button"
                    onClick={resetForm}
                    className="text-gray-500"
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="bg-blue-600 text-white px-4 py-2 rounded"
                  >
                    Simpan
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Modal Konfirmasi Hapus */}
        {confirmDeleteId && (
          <div className="fixed inset-0 z-50 bg-black/30 flex items-center justify-center">
            <div className="bg-white p-6 rounded shadow-lg max-w-sm w-full">
              <h2 className="text-lg font-semibold text-red-600 mb-4">
                Konfirmasi Hapus
              </h2>
              <p className="mb-6 text-sm text-gray-700">
                Apakah Anda yakin ingin menghapus akun ini?
              </p>
              <div className="flex justify-end gap-2">
                <button
                  onClick={() => setConfirmDeleteId(null)}
                  className="text-gray-600 px-4 py-2"
                >
                  Batal
                </button>
                <button
                  onClick={handleDelete}
                  className="bg-red-600 text-white px-4 py-2 rounded"
                >
                  Hapus
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
}
