import React, { useEffect, useState, useCallback } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

const ChartOfAccounts = () => {
  const today = new Date().toISOString().split('T')[0];
  const [tipeAkuns, setTipeAkuns] = useState([]);
  const [currencies, setCurrencies] = useState([]);
  const [chartAccounts, setChartAccounts] = useState([]);
  const [subAccounts, setSubAccounts] = useState([]);
  const [filterTipe, setFilterTipe] = useState('');
  const [search, setSearch] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [sortBy, setSortBy] = useState(null);
  const [sortOrder, setSortOrder] = useState('asc');
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);
  const [selectedTipeAkun, setSelectedTipeAkun] = useState(null);

  const [formData, setFormData] = useState({
    nama: '',
    tipe_akun_id: '',
    nomor_akun: '',
    sub_account_of: '',
    saldo_awal: '',
    kalender: today,
    currency_id: '',
    isSub: false,
  });

  const fetchAccounts = useCallback(async () => {
    try {
      const res = await api.get('/api/chart-of-accounts', {
        params: { tipe: filterTipe, search },
      });
      setChartAccounts(res.data);
      setSubAccounts(res.data);
    } catch (error) {
      console.error('Gagal memuat akun:', error.message);
    }
  }, [filterTipe, search]);

  // Fetch initial data
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const [tipeRes, currRes] = await Promise.all([
          api.get('/api/tipe-akun'),
          api.get('/api/currencies'),
        ]);
        setTipeAkuns(tipeRes.data);
        setCurrencies(currRes.data);
      } catch (error) {
        console.error('Gagal memuat data initial:', error);
      }
    };

    fetchInitialData();
  }, []);

  useEffect(() => {
    if (formData.tipe_akun_id && tipeAkuns.length > 0) {
      const found = tipeAkuns.find(
        (t) => t.id === parseInt(formData.tipe_akun_id)
      );
      if (found) {
        setSelectedTipeAkun(found);
      }
    }
  }, [formData.tipe_akun_id, tipeAkuns]);
  

  // Fetch accounts with debounce
  useEffect(() => {
    const delayDebounce = setTimeout(() => {
      fetchAccounts();
    }, 500);

    return () => clearTimeout(delayDebounce);
  }, [search, filterTipe, fetchAccounts]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    let updatedForm = {
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    };

    if (name === 'tipe_akun_id') {
      // Update selected tipe akun
      const selectedTipe = tipeAkuns.find((t) => t.id === parseInt(value));
      setSelectedTipeAkun(selectedTipe);

      // Reset currency if not required
      if (!selectedTipe?.is_currency_required) {
        updatedForm.currency_id = '';
      }

      // Reset saldo_awal if not required
      if (!selectedTipe?.is_opening_balance_required) {
        updatedForm.saldo_awal = '';
      }

      // Generate nomor akun
      if (!formData.isSub) {
        updatedForm.nomor_akun = generateNomorAkun(value, false);
      }
    }

    if (name === 'isSub') {
      updatedForm.sub_account_of = '';
      updatedForm.nomor_akun = '';
    }

    if (name === 'sub_account_of') {
      updatedForm.nomor_akun = generateNomorAkun(
        formData.tipe_akun_id,
        true,
        value
      );
    }

    setFormData(updatedForm);
  };

  const generateNomorAkun = (tipeId, isSub = false, indukId = null) => {
    if (isSub && indukId) {
      const induk = chartAccounts.find((a) => a.id === parseInt(indukId));
      if (!induk) return '';

      const [seg1, seg2] = induk.nomor_akun.split('-');

      const subList = chartAccounts
        .filter((a) => a.sub_account_of === induk.id)
        .map((a) => parseInt(a.nomor_akun.split('-')[2]));

      const baseNumber =
        Math.floor(parseInt(induk.nomor_akun.split('-')[2]) / 1000) * 1000 +
        100;

      const nextSubNumber =
        subList.length > 0 ? Math.max(...subList) + 100 : baseNumber;

      return `${seg1}-${seg2}-${nextSubNumber}`;
    } else {
      const tipe = tipeAkuns.find((t) => t.id === parseInt(tipeId));
      if (!tipe) return '';

      const kode = tipe.kode || '1-1';
      const [seg1, seg2] = kode.split('-');
      const safeSeg1 = seg1 || '1';
      const safeSeg2 = seg2 || '1';

      const akunUtama = chartAccounts.filter(
        (a) => a.tipe_akun_id === parseInt(tipeId) && !a.sub_account_of
      );

      const existingMainNumbers = akunUtama.map((a) =>
        parseInt(a.nomor_akun.split('-')[2])
      );

      const nextMainNumber =
        existingMainNumbers.length > 0
          ? Math.max(...existingMainNumbers) + 1000
          : 1000;

      return `${safeSeg1}-${safeSeg2}-${nextMainNumber}`;
    }
  };

  const validateForm = () => {
    if (!formData.nama.trim()) {
      alert('Nama akun wajib diisi.');
      return false;
    }

    if (!formData.tipe_akun_id) {
      alert('Tipe akun wajib dipilih.');
      return false;
    }

    if (!formData.kalender) {
      alert('Tanggal wajib diisi.');
      return false;
    }

    if (formData.isSub && !formData.sub_account_of) {
      alert('Pilih akun induk jika ini sub akun.');
      return false;
    }

    // Validasi berdasarkan tipe akun
    if (selectedTipeAkun) {
      if (selectedTipeAkun.is_currency_required && !formData.currency_id) {
        alert('Mata uang wajib dipilih untuk tipe akun ini.');
        return false;
      }

      if (
        selectedTipeAkun.is_opening_balance_required &&
        (!formData.saldo_awal || formData.saldo_awal === '')
      ) {
        alert('Saldo awal wajib diisi untuk tipe akun ini.');
        return false;
      }
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    // Check duplicate nomor akun
    const isDuplicateNomor = chartAccounts.some(
      (a) => a.nomor_akun === formData.nomor_akun && a.id !== editingId
    );

    if (isDuplicateNomor) {
      return alert(
        'Nomor akun sudah digunakan. Silakan pilih/ganti nomor lain.'
      );
    }

    const payload = {
      nama: formData.nama,
      tipe_akun_id: formData.tipe_akun_id,
      nomor_akun: formData.nomor_akun,
      sub_account_of: formData.isSub ? formData.sub_account_of : null,
      saldo_awal:
        formData.saldo_awal === '' ? 0 : parseFloat(formData.saldo_awal),
      kalender: formData.kalender,
      currency_id: formData.currency_id || null,
    };

    try {
      if (editingId) {
        await api.put(`/api/chart-of-accounts/${editingId}`, payload);
      } else {
        await api.post('/api/chart-of-accounts', payload);
      }

      resetForm();
      setShowModal(false);
      fetchAccounts();
    } catch (err) {
      console.error('Error:', err);
      if (err.response?.data?.errors) {
        const errors = err.response.data.errors;
        const errorMessages = Object.values(errors).flat().join('\n');
        alert('Validation errors:\n' + errorMessages);
      } else if (err.response?.data?.message) {
        alert('Error: ' + err.response.data.message);
      } else {
        alert('Gagal menyimpan data.');
      }
    }
  };

  const resetForm = () => {
    setFormData({
      nama: '',
      tipe_akun_id: '',
      nomor_akun: '',
      sub_account_of: '',
      saldo_awal: '',
      kalender: today,
      currency_id: '',
      isSub: false,
    });
    setEditingId(null);
    setSelectedTipeAkun(null);
  };

  const handleEdit = (akun) => {
    const selectedTipe = tipeAkuns.find((t) => t.id === akun.tipe_akun_id);
    setSelectedTipeAkun(selectedTipe);

    setFormData({
      nama: akun.nama,
      tipe_akun_id: akun.tipe_akun_id,
      nomor_akun: akun.nomor_akun,
      sub_account_of: akun.sub_account_of || '',
      saldo_awal: akun.saldo_awal || '',
      kalender: akun.kalender,
      currency_id: akun.currency_id || '',
      isSub: !!akun.sub_account_of,
    });
    setEditingId(akun.id);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    setConfirmDeleteId(id);
  };

  const confirmDelete = async () => {
    try {
      await api.delete(`/api/chart-of-accounts/${confirmDeleteId}`);
      setConfirmDeleteId(null);
      fetchAccounts();
    } catch (err) {
      console.error(err);
      if (err.response?.data?.message) {
        alert('Error: ' + err.response.data.message);
      } else {
        alert('Gagal menghapus akun.');
      }
    }
  };

  const handleSort = (column) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortOrder('asc');
    }
  };

  const buildAccountTree = (list, parentId = null, level = 0) => {
    return list
      .filter((a) => a.sub_account_of === parentId)
      .flatMap((a) => [
        { ...a, level },
        ...buildAccountTree(list, a.id, level + 1),
      ]);
  };

  const filteredChartAccounts = chartAccounts.filter((a) => {
    const matchTipe = filterTipe
      ? String(a.tipe_akun_id) === String(filterTipe)
      : true;
    const matchSearch = search
      ? a.nama.toLowerCase().includes(search.toLowerCase())
      : true;
    return matchTipe && matchSearch;
  });

  const treeData = buildAccountTree(filteredChartAccounts);
  const sortedTreeData = [...treeData].sort((a, b) => {
    if (!sortBy) return 0;

    const valA = sortBy === 'tipe' ? a.tipe_akun?.nama || '' : a[sortBy] || '';
    const valB = sortBy === 'tipe' ? b.tipe_akun?.nama || '' : b[sortBy] || '';

    if (typeof valA === 'string') {
      return sortOrder === 'asc'
        ? valA.localeCompare(valB)
        : valB.localeCompare(valA);
    }

    if (typeof valA === 'number') {
      return sortOrder === 'asc' ? valA - valB : valB - valA;
    }

    return 0;
  });

  const getCurrencyDisplay = (account) => {
    if (account.currency) {
      return `${account.currency.symbol} ${account.currency.name}`;
    }
    return '-';
  };

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Chart of Accounts</h2>
          <button
            onClick={() => {
              resetForm();
              setShowModal(true);
            }}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            + New Account
          </button>
        </div>

        <div className="mb-4 flex gap-2">
          <select
            onChange={(e) => setFilterTipe(e.target.value)}
            className="border p-2 rounded"
            value={filterTipe}
          >
            <option value="">All Types</option>
            {tipeAkuns.map((tipe) => (
              <option key={tipe.id} value={tipe.id}>
                {tipe.nama}
              </option>
            ))}
          </select>
          <input
            placeholder="Search by name"
            onChange={(e) => setSearch(e.target.value)}
            className="border p-2 rounded flex-1"
            value={search}
          />
        </div>

        <div className="overflow-x-auto">
          <table className="w-full border border-gray-200">
            <thead>
              <tr className="bg-gray-100">
                <th
                  className="border p-2 cursor-pointer hover:bg-gray-200"
                  onClick={() => handleSort('nomor_akun')}
                >
                  Account No{' '}
                  {sortBy === 'nomor_akun' && (sortOrder === 'asc' ? '↑' : '↓')}
                </th>
                <th className="border p-2">Name</th>
                <th
                  className="border p-2 cursor-pointer hover:bg-gray-200"
                  onClick={() => handleSort('tipe')}
                >
                  Type {sortBy === 'tipe' && (sortOrder === 'asc' ? '↑' : '↓')}
                </th>
                <th className="border p-2">Currency</th>
                <th className="border p-2">Balance</th>
                <th className="border text-center p-2">Action</th>
              </tr>
            </thead>
            <tbody>
              {sortedTreeData.length > 0 ? (
                sortedTreeData.map((a) => (
                  <tr key={a.id} className="hover:bg-gray-50">
                    <td className="border p-2">
                      {' '.repeat(a.level * 4) + a.nomor_akun}
                    </td>
                    <td className="border p-2">
                      {' '.repeat(a.level * 4) + a.nama}
                    </td>
                    <td className="border p-2">{a.tipe_akun?.nama}</td>
                    <td className="border p-2">{getCurrencyDisplay(a)}</td>
                    <td className="border p-2 text-right">
                      {parseFloat(a.saldo_awal || 0).toLocaleString('id-ID')}
                    </td>
                    <td className="border p-2 text-center space-x-2">
                      <button
                        onClick={() => handleEdit(a)}
                        className="bg-blue-50 hover:bg-blue-100 text-blue-600 hover:text-blue-800 px-3 py-1 rounded-md transition duration-200"
                        title="Edit"
                      >
                        <i className="bi bi-pencil-fill"></i>
                      </button>
                      <button
                        onClick={() => handleDelete(a.id)}
                        className="bg-red-50 hover:bg-red-100 text-red-600 hover:text-red-800 px-3 py-1 rounded-md transition duration-200"
                        title="Hapus"
                      >
                        <i className="bi bi-trash-fill"></i>
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" className="text-center text-gray-500 py-4">
                    Data tidak ditemukan
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        {/* Modal Form */}
        {showModal && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/30">
            <div className="bg-white p-6 rounded-lg w-full max-w-lg relative shadow-xl max-h-[90vh] overflow-y-auto">
              <button
                className="absolute top-2 right-2 text-gray-500 hover:text-black text-xl"
                onClick={() => {
                  setShowModal(false);
                  resetForm();
                }}
              >
                ✕
              </button>
              <h3 className="text-lg font-semibold mb-4">
                {editingId ? 'Edit Account' : 'Create New Account'}
              </h3>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium mb-1">
                    Nama Akun <span className="text-red-500">*</span>
                  </label>
                  <input
                    name="nama"
                    value={formData.nama}
                    onChange={handleChange}
                    placeholder="Masukkan nama akun"
                    className="border border-gray-300 p-2 w-full rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1">
                    Tipe Akun <span className="text-red-500">*</span>
                  </label>
                  <select
                    name="tipe_akun_id"
                    value={formData.tipe_akun_id}
                    onChange={handleChange}
                    className="border border-gray-300 p-2 w-full rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  >
                    <option value="">-- Pilih Tipe Akun --</option>
                    {tipeAkuns.map((type) => (
                      <option key={type.id} value={type.id}>
                        {type.nama}
                        {type.is_currency_required && ' (Wajib Mata Uang)'}
                        {type.is_opening_balance_required &&
                          ' (Wajib Saldo Awal)'}
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1">
                    Nomor Akun <span className="text-red-500">*</span>
                  </label>
                  <input
                    name="nomor_akun"
                    value={formData.nomor_akun}
                    onChange={handleChange}
                    placeholder="Nomor akun akan digenerate otomatis"
                    className="border border-gray-300 p-2 w-full rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                </div>

                <div>
                  <label className="flex items-center gap-2 cursor-pointer">
                    <input
                      type="checkbox"
                      name="isSub"
                      checked={formData.isSub}
                      onChange={handleChange}
                      className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                    />
                    <span className="text-sm font-medium">Sub Account of</span>
                  </label>
                </div>

                {formData.isSub && (
                  <div>
                    <label className="block text-sm font-medium mb-1">
                      Akun Induk <span className="text-red-500">*</span>
                    </label>
                    <select
                      name="sub_account_of"
                      value={formData.sub_account_of}
                      onChange={handleChange}
                      className="border border-gray-300 p-2 w-full rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                      required={formData.isSub}
                    >
                      <option value="">-- Pilih Akun Induk --</option>
                      {subAccounts
                        .filter(
                          (p) =>
                            p.tipe_akun_id ===
                              parseInt(formData.tipe_akun_id) &&
                            !p.sub_account_of &&
                            p.id !== editingId
                        )
                        .map((p) => (
                          <option key={p.id} value={p.id}>
                            {p.nomor_akun} - {p.nama}
                          </option>
                        ))}
                    </select>
                  </div>
                )}

                {/* Currency Field - Show only if required or if already selected */}
                {(selectedTipeAkun?.is_currency_required ||
                  formData.currency_id) && (
                  <div>
                    <label className="block text-sm font-medium mb-1">
                      Mata Uang{' '}
                      {selectedTipeAkun?.is_currency_required && (
                        <span className="text-red-500">*</span>
                      )}
                    </label>
                    <select
                      name="currency_id"
                      value={formData.currency_id}
                      onChange={handleChange}
                      className="border border-gray-300 p-2 w-full rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                      required={selectedTipeAkun?.is_currency_required}
                    >
                      <option value="">-- Pilih Mata Uang --</option>
                      {currencies.map((currency) => (
                        <option key={currency.id} value={currency.id}>
                          {currency.symbol} - {currency.name} (
                          {currency.country})
                        </option>
                      ))}
                    </select>
                  </div>
                )}

                {/* Add Currency Selection for non-required types */}
                {!selectedTipeAkun?.is_currency_required &&
                  !formData.currency_id &&
                  selectedTipeAkun && (
                    <div>
                      <label className="block text-sm font-medium mb-1">
                        Mata Uang (Opsional)
                      </label>
                      <select
                        name="currency_id"
                        value={formData.currency_id}
                        onChange={handleChange}
                        className="border border-gray-300 p-2 w-full rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                      >
                        <option value="">-- Pilih Mata Uang --</option>
                        {currencies.map((currency) => (
                          <option key={currency.id} value={currency.id}>
                            {currency.symbol} - {currency.name} (
                            {currency.country})
                          </option>
                        ))}
                      </select>
                    </div>
                  )}

                <div>
                  <label className="block text-sm font-medium mb-1">
                    Saldo Awal{' '}
                    {selectedTipeAkun?.is_opening_balance_required && (
                      <span className="text-red-500">*</span>
                    )}
                  </label>
                  <input
                    type="number"
                    name="saldo_awal"
                    value={formData.saldo_awal}
                    onChange={handleChange}
                    placeholder="Masukkan saldo awal"
                    className="border border-gray-300 p-2 w-full rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    step="0.01"
                    required={selectedTipeAkun?.is_opening_balance_required}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1">
                    Tanggal <span className="text-red-500">*</span>
                  </label>
                  <input
                    type="date"
                    name="kalender"
                    value={formData.kalender}
                    onChange={handleChange}
                    className="border border-gray-300 p-2 w-full rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                </div>

                {/* Info about requirements */}
                {selectedTipeAkun && (
                  <div className="bg-blue-50 border border-blue-200 rounded p-3">
                    <h4 className="font-medium text-blue-800 mb-2">
                      Persyaratan Tipe Akun:
                    </h4>
                    <ul className="text-sm text-blue-700 space-y-1">
                      <li>
                        • Mata Uang:{' '}
                        {selectedTipeAkun.is_currency_required
                          ? 'Wajib'
                          : 'Opsional'}
                      </li>
                      <li>
                        • Saldo Awal:{' '}
                        {selectedTipeAkun.is_opening_balance_required
                          ? 'Wajib'
                          : 'Opsional'}
                      </li>
                    </ul>
                  </div>
                )}

                <div className="flex justify-end gap-2 pt-4">
                  <button
                    type="button"
                    onClick={() => {
                      setShowModal(false);
                      resetForm();
                    }}
                    className="px-4 py-2 text-gray-600 hover:text-gray-800 border border-gray-300 rounded hover:bg-gray-50"
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    {editingId ? 'Update' : 'Simpan'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Modal Konfirmasi Hapus */}
        {confirmDeleteId && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/30">
            <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-sm">
              <h2 className="text-lg font-semibold mb-4 text-red-600">
                Konfirmasi Hapus
              </h2>
              <p className="mb-6 text-sm text-gray-700">
                Apakah Anda yakin ingin menghapus akun ini? Tindakan ini tidak
                dapat dibatalkan.
              </p>
              <div className="flex justify-end gap-3">
                <button
                  onClick={() => setConfirmDeleteId(null)}
                  className="px-4 py-2 text-gray-600 hover:text-gray-800 border border-gray-300 rounded hover:bg-gray-50"
                >
                  Batal
                </button>
                <button
                  onClick={confirmDelete}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded focus:outline-none focus:ring-2 focus:ring-red-500"
                >
                  Hapus
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default ChartOfAccounts;
